@use 'reset' as *;
@use 'breakpoints' as *;
@use 'colors' as *;
@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans:wght@400;700&display=swap');

@mixin buttons($color, $hover) {
  border-radius: 12px;
  background: $color;
  border: none;
  font-weight: 700;
  display: flex;
  justify-content: center;
  gap: 7.92px;
  font-size: 18px;
  padding: 15px 59px;

  &:hover {
    background: $hover;
  }
}

@mixin space {
  padding: 0 24px;

  @include md {
    padding: 0 39px;
  }

  @include xl {
    padding: 0 165px;
  }
}



