sources.scss/breakpoints/_breakpoints.scss:
$breakpoints: (
  xs: 0,
  sm: 576px,
  //tablet
  md: 768px,
  lg: 992px,
  //desktop
  xl: 1440px,
);

@mixin xs {
  @media (min-width: map-get($breakpoints, "xs")){
    @content;
  }
}

@mixin sm {
  @media (min-width: map-get($breakpoints, "sm")){
    @content;
  }
}

@mixin md {
  @media (min-width: map-get($breakpoints, "md")){
    @content;
  }
}

@mixin lg {
  @media (min-width: map-get($breakpoints, "lg")){
    @content;
  }
}

@mixin xl {
  @media (min-width: map-get($breakpoints, "xl")){
    @content;
  }
}

// If breakpoint is not defined, use default breakpoint

@mixin breakpoint($bp: 0) {
  @media (min-width: $bp) {
    @content;
  }
}
